mapper.select:
Select		@rownum:=@rownum-1 as rnum,a.*
from 		board as a,(select @rownum:=7) tmp
order by 	rnum;

select *
from(
		Select		@rownum:=@rownum-1 as rnum,a.*
		from 		board as a,(select @rownum:=7) tmp
		order by 	rnum
)as b
where rnum between ((#{nowpage}-1)*#{pageScale})+1 and ((#{nowpage}-1)*#{pageScale})+#{pageScale}
